          if (event.keyCode === 32) {
              $("#color-generator").click();
          } // end if
      }); // end $(tabs).keyup
}

  componentDidUpdate() {
    var colorPanel = document.getElementById("color-list");
    colorPanel.scrollTo(0,colorPanel.scrollHeight);
  }

  colorRollback(clickedObject) {
    var bgRollbackColor = $(clickedObject.target).css("backgroundColor");
    $('#color-generator').css("background-color", bgRollbackColor);
  }

  toggleGrayscale() {
    if (this.state.generateGrayscale) {
      this.setState({ generateGrayscale: false });
      $('#grayscale').css("color","#FFFFFF");
    } else {
      this.setState({ generateGrayscale: true });
      $('#grayscale').css("color","#000000");
    }
  }

toggleColorType() {
  if (this.state.showHexColor) {
    this.setState({ showHexColor: false });
    $('#color-type').html("HEX");
  } else {
    this.setState({ showHexColor: true });
    $('#color-type').html("RGB");
  }
}

  toggleColorPanel() {
    var heightToMove = $('#history-container').css("height");
    var controlsHeight = $('#buttons').css("height");
    controlsHeight =  parseInt(controlsHeight.substring(0,heightToMove.length-2),10);
    heightToMove = parseInt(heightToMove.substring(0,heightToMove.length-2),10);
    heightToMove = heightToMove - controlsHeight + 2;

    if (this.state.isVisible) {
      $( "#history-container" ).animate({ bottom: "-" + heightToMove }, 500);
      $('#color-list').addClass("no-box-shadow");
      $('#history-arrow').html("&and;");
      this.setState({ isVisible: false });
    } else {
      $( "#history-container" ).animate({ bottom:"0" }, 500);
      $('#color-list').removeClass("no-box-shadow");
      $('#history-arrow').html("&or;");
      this.setState({ isVisible: true });
    }
  }

  render() {
    if(this.state.colors.length > 0) {
      var bgColor = this.state.colors[this.state.colors.length - 1].hex;
  }
    const colorList = this.state.colors.map( (color, index) =>
    <Color key={index} hex={color.hex} function={this.colorRollback}/>)
    return (
    <div id="color-generator" style={{backgroundColor:bgColor}} onClick={this.anotherColor}>
      <p id="howto-text" onClick={this.anotherColor}>Click, Tap, or Press <code>space</code></p>
      <div id="history-container">
        <div id="buttons">
        <p id="history-arrow" onClick={this.toggleColorPanel}>&or;</p>
        <p id="grayscale" onClick={this.toggleGrayscale}>&#9728;</p>
        <p id="color-type" onClick={this.toggleColorType}>{this.state.colorType}</p>
        </div>  { /* end buttons */ }
      <div id="color-list">
        <div id="history-text">
          <p>Your color history will appear here.</p>
          <p>Click on a color to return to it.</p>
        </div>
        {colorList}
        </div> { /* end color-list */ }
        </div> { /* end color-container && end color-generator on line below */ }
    </div>
  )
}
}

export { ColorGenerator };
