import React, { PureComponent } from 'react';
import './App.css';
import { Color } from './components/Color'
import { disableBodyScroll } from 'body-scroll-lock';
import $ from 'jquery';

class ColorGenerator extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      colors: []
    };
    this.anotherColor = this.anotherColor.bind(this);
    this.colorRollback = this.colorRollback.bind(this);
  }

  anotherColor() {
    $("#howto-text").remove();
    $("#history-text").remove();
    var colorHex = this.generateHex();
    this.setState({
      colors: this.state.colors.concat(
            {
              hex:colorHex
            }) // end concat()
          }); //end setState
    }

    generateHex() {
      var generatedColor = Math.floor(Math.random()*16777215).toString(16).toUpperCase();
      !($(this.attr('id') == 'colors-panel'))
      while (generatedColor.length < 6) {
        generatedColor = Math.floor(Math.random()*16777215).toString(16).toUpperCase();
      }
      return "#" + generatedColor;
    }

componentDidMount() {
  disableBodyScroll(document.body);
  }

  colorRollback() {
    alert("Color Rollback Test");
  }

  render() {
    if(this.state.colors.length > 0) {
      var clr = this.state.colors[this.state.colors.length - 1].hex;
  }
    const colorList = this.state.colors.map( (color, index) =>
    <Color key={index} hex={color.hex} function={this.colorRollback}/>)
    return(
    <div id="color-generator-app" style={{backgroundColor:clr}} onClick={this.anotherColor}>
      <p id="howto-text">Click anyhere on the screen to generate a new color.</p>
      <div className="colors-panel">
        <p id="history-text">Your color history will appear here. Click on a color to return to it.</p>
        {colorList}
      </div>
    </div>

  )
}
}

export { ColorGenerator };
