import React, { PureComponent } from 'react';
import { Color } from './Color'
import { disableBodyScroll, enableBodyScroll, clearAllBodyScrollLocks } from 'body-scroll-lock';


class ColorList extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      colors: [
        {
          id:0,
          hex:"FFFFFF"
        }
      ]
    };
    this.anotherColor = this.anotherColor.bind(this);
  }

  anotherColor() {
    var colorHex = this.generateHex();
    this.setState({
      colors: this.state.colors.concat(
            {
              hex:colorHex
            }) // end concat()
          }); //end setState
      console.log(this.state);
    }

    generateHex() {
      return "#" + Math.floor(Math.random()*16777215).toString(16).toUpperCase();
    }

    componentDidMount() {}

    targetRef = React.createRef();
  targetElement = document.body;

  componentDidMount() {
    // 3. Get a target element that you want to persist scrolling for (such as a modal/lightbox/flyout/nav).
    // Specifically, the target element is the one we would like to allow scroll on (NOT a parent of that element).
    // This is also the element to apply the CSS '-webkit-overflow-scrolling: touch;' if desired.
    this.targetElement = this.targetRef.current;
  }

  showTargetElement = () => {
    // ... some logic to show target element

    // 4. Disable body scroll
    disableBodyScroll(this.targetElement);
  }

  render() {
    console.log(this.state.colors.length)
    const colorList = this.state.colors.map( (color) =>
    <Color key={this.state.colors.length-1} hex={color.hex}/>)
    return(
    <>
      <div className="colors-panel">
      {colorList}
      </div>
      <button onClick={this.anotherColor}>New Color</button>
    </>

  )
}
}

export { ColorList };
