import { SHOW_HEX, SHOW_RGB } from './colorTypes';

const initialState = {
  colorType: "HEX"
}

export const colorReducer = (state = initialState, action) => {
  switch(action.type) {
    case SHOW_HEX:
    return {
      ... state,
      colorType: "HEX"
    }
    case SHOW_RGB:
    return {
      ... state,
      colorType: "RGB"
    }

    default: return state
  }
};
