import { SHOW_HEX, SHOW_RGB, TOGGLE_HISTORY } from './colorTypes';

const initialState = {
  colors: [],
  historyVisible: true,
  generateGrayscale: false,
  colorType: "HEX"
}

export const colorReducer = (state = initialState, action) => {
  switch(action.type) {
    case SHOW_HEX:
    return {
      ... state,
      colorType: "HEX"
    }
    case SHOW_RGB:
    return {
      ... state,
      colorType: "RGB"
    }
    case TOGGLE_HISTORY:
    if (state.historyVisible) {
      $( "#history-container" ).animate({ bottom: "-" + heightToMove }, 500);
      $('#color-list').addClass("no-box-shadow");
      $('#history-arrow').html("&and;");
      console.log(state)
    return {
      ... state,
      historyVisible: false
    }
  } else {
    return {
      ... state,
      isVisible: true
    }
    }


    default: return state
  }
};
