import React, { PureComponent } from 'react';
import { Color } from './Color'

class ColorList extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      colors: [
        {
          id:0,
          hex:"FFFFFF"
        }
      ]
    };
    this.anotherColor = this.anotherColor.bind(this);
  }

  anotherColor() {
    var colorHex = this.generateHex();
    this.setState({
      colors: this.state.colors.concat(
            {
              hex:colorHex
            }) // end concat()
          }); //end setState
      console.log(this.state);
    }

    generateHex() {
      return Math.floor(Math.random()*16777215).toString(16);
    }

    componentDidMount() {}

  render() {
    console.log(this.state.colors.length)
    const colorList = this.state.colors.map( (color) =>
    <Color key={this.state.colors.length-1} hex={color.hex}/>)
    return(
    <>
      <div className="colors-panel">
      {colorList}
      </div>
      <button onClick={this.anotherColor}>New Color</button>
    </>

  )
}
}

export { ColorList };
