import React, { PureComponent } from 'react';
import './App.css';
import { ColorList } from './components/ColorList'
import { Color } from './components/Color'
import { disableBodyScroll } from 'body-scroll-lock';

class ColorGenerator extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      colors: [
        {
          id:0,
          hex:"FFFFFF"
        }
      ]
    };
    this.anotherColor = this.anotherColor.bind(this);
  }

  anotherColor() {
    var colorHex = this.generateHex();
    this.setState({
      colors: this.state.colors.concat(
            {
              hex:colorHex
            }) // end concat()
          }); //end setState
      console.log(this.state);
    }

    generateHex() {
      return "#" + Math.floor(Math.random()*16777215).toString(16).toUpperCase();
    }

componentDidMount() {
  disableBodyScroll(document.body);
  }

  render() {
    const colorList = this.state.colors.map( (color, index) =>
    <Color key={index} hex={color.hex} index={index}/>)
    return(
    <>
      <div className="colors-panel">
      {colorList}
      </div>
      <button onClick={this.anotherColor}>New Color</button>
    </>

  )
}
}

export { ColorGenerator };
