import React, { PureComponent } from 'react';
import { Color } from './Color'

class ColorList extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      numberOfColors: 1,
      colors: [
        {
          id:0,
          hex:"FFFFFF"
        }
      ]
    };
    this.anotherColor = this.anotherColor.bind(this);
  }

  anotherColor() {
    this.increment();
    console.log(this.state.numberOfColors);
    this.setState({
      colors: this.state.colors.concat(
            {
              id:this.state.numberOfColors,
              name:"Another Name"
            }) // end concat()
          }); //end setState
      console.log(this.state);
    }

    increment() {
      this.setState({ numberOfColors: this.state.numberOfColors + 1 });
    }

  render() {
    const colorList = colors.map( (color) =>
    <Color key={this.state.numberOfColors} hex={color.hex}/>)
    return(
    <>
      <div className="colors-panel">
      {colorList}
      </div>
      <button onClick={this.anotherColor}>New Color</button>
    </>

  )
}
}

export { ColorList };
